import React, { FunctionComponent, useState, useEffect } from 'react';
import { RouteComponentProps, Link } from 'react-router-dom';
import Pokemon from '../models/pokemon';
import formatDate from '../helpers/format-date';
import formatType from '../helpers/format-type';
import PokemonService from '../services/pokemon-service';
  
type Params = { id: string }; //on definit un type params avec un elt id de type string
  
const PokemonsDetail: FunctionComponent<RouteComponentProps<Params>> = ({ match }) => {

  //1. ici on utilise l'importation RouteComponentProps pour typer le parametre reçu depuis le router c-à-d une donnée reçu depuis l'URL.

  //2.le routeur de react va associer la prop issuue de l'URL à un objet match qui contient les données passées par le router
  const [pokemon, setPokemon] = useState<Pokemon|null>(null);
  
  useEffect(() => {
   
  }, [match.params.id]);
  //1.le Hook d'effect ci-haut avec la boucle forEach permet de retrouver le pokemon avec l'id qui est passé en parametre.
  //2. si un pokemon n'est pas trouvé celui-ci vaut la valeur null.
  return (
    <div>
      { pokemon ? (
        //on définit un opérateur ténaire pour vérifier qu'on a bien trouvé un pkemon correspondant à l'id. si c'est le cas on l'affiche sinon on renvoie un message: Aucun pokémon à afficher !
        //voir le DOM virtuel
        <div className="row">
          <div className="col s12 m8 offset-m2"> 
            <h2 className="header center">{ pokemon.name }</h2>
            <div className="card hoverable"> 
              <div className="card-image">
                <img src={pokemon.picture} alt={pokemon.name} style={{width: '250px', margin: '0 auto'}}/>
                  
                <Link to={`/pokemons/edit/${pokemon.id}`} className='btn btn-floating halfway-fab waves-effect waves-light'>
                <i className='material-icons'>edit
                  </i>
                </Link>
              </div>
              <div className="card-stacked">
                <div className="card-content">
                  <table className="bordered striped">
                    <tbody>
                      <tr> 
                        <td>Nom</td> 
                        <td><strong>{ pokemon.name }</strong></td> 
                      </tr>
                      <tr> 
                        <td>Points de vie</td> 
                        <td><strong>{ pokemon.hp }</strong></td> 
                      </tr> 
                      <tr> 
                        <td>Dégâts</td> 
                        <td><strong>{ pokemon.cp }</strong></td> 
                      </tr> 
                      <tr> 
                        <td>Types</td> 
                        <td>
                          {pokemon.types.map(type => (
                           <span key={type} className={formatType(type)}>{type}</span>
                          ))}</td> 
                      </tr> 
                      <tr> 
                        <td>Date de création</td> 
                        <td>{formatDate(pokemon.created)}</td> 
                      </tr>
                    </tbody>
                  </table>
                </div>
                <div className="card-action">
                  <Link to="/">Retour</Link>
                  {/* ici on utilise l'elt Link qui permet generer un lien et on doit lui passer une valeur correspondant au chemin vers lequel on aimerait rediriger nos users via la prop to. 
                  Dans notre cas on redirige nos users vers pokemon-list.tsx du coup on passe un slash to='/'*/}
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <h4 className="center" style={{color:'#FF0000'}}>Aucun pokémon à afficher !</h4>
      )}
    </div>
  );
}
  
export default PokemonsDetail;
